// LOOPS ( repetition )
// 1) start
// 2) stop
// 3) step ( connect start to stop )

// 1, 3, 5, 7, 9
// start  === 1
// stop ==== 9
// step === 2

// 21, 18, 15, 12, 9, 6, 3, 2
// start ====> 21
// stop ===>  2
// step ===> -3

// types of loop ( for, while )

/**
 * 
 *  for ( start; stop; step )
 *  {
 *  
 *  }
 */

console.log( "Start Counting" )
console.log( 0 )
console.log( 1 )
console.log( 2 )
console.log( 3 )
console.log( 4 )
console.log( 5 )
console.log( 6 )
console.log( 7 )
console.log( 8 )
console.log( 9 )
console.log( 10 )

console.log( "The Loop will start counting ")

// increasing
for ( let i = 0; i < 11; i += 2 ){
    console.log( i )
}

console.log( "The Loop will start decreasing ")

// decreasing

for ( let k = 21; k > 2; k -= 3) {
    console.log( k )
}

for ( let j = 0; j < nigeriaBanksData.length; j++)
{
    console.log ( nigeriaBanksData[j].logo )
    console.log ( nigeriaBanksData[j].ussd )
    console.log( nigeriaBanksData[j].name )
}

console.log( "The Nigeria Banks Printing Backwards ")


for ( let index = nigeriaBanksData.length - 1; index >= 0; index -= 1 ){
    
    console.log( index )

    console.log ( nigeriaBanksData[index].name )
    console.log ( nigeriaBanksData[index].ussd )
    console.log( nigeriaBanksData[index].logo )
}

// while loop
/**
 *  start
 *  while ( stop ) {
 *   
 *      step
 *  }
 * 
 */

console.log( " WHILE LOOP")

let m = 0 // start
while ( m < 10 ) // stop
{
    console.log( m )
    m++ // step
}

// infinite loop
